generated.css:
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure,
footer, header, hgroup, menu, nav, section {
  display: block;
}

body {
  line-height: 1;
}

ol, ul {
  list-style: none;
}

blockquote, q {
  quotes: none;
}

blockquote:before, blockquote:after,
q:before, q:after {
  content: "";
  content: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

a {
  text-decoration: none;
}

header {
  background-image: url("25522d3a8fac0d7845b78b6ba05ab0b7.jpg");
  background-repeat: no-repeat;
  background-size: 100%;
  background-position: top;
  background-attachment: fixed;
  height: 60vh;
  width: 100vw;
}

header {
  display: flex;
  justify-content: center;
  align-items: center;
}

header div ul {
  font-family: Arial, Helvetica, sans-serif;
  color: white;
  font-size: 34px;
  background: black;
  padding: 55px;
}

header div ul li:nth-child(2) {
  font-size: 18px;
  margin-top: 40px;
}

header div ul li {
  text-align: center;
}

nav ul {
  display: flex;
  flex-direction: row;
  align-items: center;
  background: black;
  width: 100vw;
  height: 5vh;
}

nav ul li {
  padding-left: 5rem;
}

nav ul li:nth-child(1) {
  padding-left: 15%;
}

nav ul a {
  color: #b1b1b1;
  font-family: Arial, Helvetica, sans-serif;
}

nav ul a:hover {
  color: white;
  transition: 0, 5s;
}

section h1 {
  font-size: 35px;
  font-family: Arial, Helvetica, sans-serif;
  font-weight: 600;
  margin: 0 20% 0 20%;
  margin: 5% 20% 2% 20%;
}

section p {
  font-size: 20px;
  margin: 0 20% 0 20%;
  line-height: 140%;
}
